mapper.select:
SELECT *
FROM log
WHERE 1=1

    AND logDate >= #{startDate}


    AND logDate <= #{endDate}


    AND logInfo LIKE CONCAT('%', #{searchKeyword}, '%')


    AND logTarget LIKE CONCAT('%', #{searchKeyword}, '%')

ORDER BY logIdx DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM log WHERE 1=1
AND logDate >= #{startDate}
AND logDate <= #{endDate}
AND logInfo LIKE CONCAT('%', #{searchKeyword}, '%')
AND logTarget LIKE CONCAT('%', #{searchKeyword}, '%')
