mapper.select:
SELECT COUNT(*)
FROM user
WHERE userId = #{userId}
AND userPassword = #{userPassword}

SELECT userIdx, userName, userId, userPassword, userEmail, userPhone, userPoint, userGrade, userRegDate, userStatus
FROM user
WHERE userID = #{userId}

SELECT theaterIdx, theaterName, theaterRegion, theaterAddress, theaterInfo, theaterImageUrl, theaterScreenCount, theaterRegDate, theaterStatus
FROM theater
WHERE theaterIdx = #{theaterIdx}
