mapper.select:
SELECT s.*
FROM seat s
INNER JOIN timetable t ON s.screenIdx = t.screenIdx
WHERE s.screenIdx = #{screenIdx}
AND t.timetableIdx = #{timetableIdx}
AND s.seatStatus = '0'

mapper.update:
UPDATE seat
SET seatStatus = #{seatStatus}
WHERE seatIdx = #{seatIdx}

mapper.select:
SELECT seatPrice
FROM price
WHERE screenType = #{screenType}
AND ageGroup = #{ageGroup}
AND dayOfWeek = #{dayOfWeek}
AND timeOfDay = #{timeOfDay}
